import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import ProductService from "../service/ProductService";
import OrderService from "../service/OrderService";
import { toast } from "react-toastify";
//import { Link, useNavigate } from "react-router-dom";

const Checkout = () => {
  const [products, setProducts] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);

      try {
        const response = await OrderService.getAllOrders();
        setProducts(response.data);
        console.log(response.data);
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  const notify = () => {
    toast.success(`Category Added Successfully`, {
      position: toast.POSITION.TOP_RIGHT,
      autoClose: 1500,
    });
  };

  const [order, setOrder] = useState({
    firstname: "",
    lastname: "",
    email: "",
    address1: "",
    address2: "",
    paymentType: "",
    cardName: "",
    cardNumber: "",
    expiration: "",
    cvv: "",
    totalPrice: "",
  });

  // const [selects, setSelects] = useState()
  //   const navigate = useNavigate();

  const handleChange = (e) => {
    const value = e.target.value;
    setOrder({ ...order, [e.target.name]: value });
  };

  const saveOrder = (e) => {
    OrderService.saveOrder(order)
      .then((result) => {
        notify();
     <link rel="stylesheet" href="/checkout" />
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const state = useSelector((state) => state.addItem);

  var total = 0;
  const itemList = (item) => {
    total = total + item.productRetailPrice;
    return (
      <li className="list-group-item d-flex justify-content-between lh-sm">
        <div>
          <h6 className="my-0">{item.productName}</h6>
        </div>
        <span className="text-muted">Rs.{item.productRetailPrice}</span>
      </li>
    );
  };

  return (

      <div className="container my-5">
        <div className="row g-5">
          <div className="col-md-5 col-lg-4 order-md-last">
            <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text" style={{ color: "white" }}>
                Your cart
              </span>
              <span className="badge bg-warning rounded-pill">
                {state.length}
              </span>
            </h4>
            <ul className="list-group mb-3">
              {state.map(itemList)}

              <li className="list-group-item d-flex justify-content-between">
                <span>Total</span>
                <strong>Rs.{total}</strong>
              </li>
            </ul>

            {/* <form className="card p-2">
                            <div className="input-group">
                                <input type="text" className="form-control" placeholder="Promo code" />
                                <button type="submit" className="btn btn-secondary">Redeem</button>
                            </div>
                        </form> */}
          </div>
          </div>
          <div className="content-wrapper" style={{background:"#181a1b"}}>
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-6 offset-md-3">
              <h1 className="m-0 text-center text-white">Add Billing Details</h1>
            </div>
            {/* <div className="col-sm-6">
              <ol className="breadcrumb float-sm-right">
                <li className="breadcrumb-item">
                  <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item active">Dashboard</li>
              </ol>
            </div> */}
          </div>
        </div>
      </div>
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6 offset-md-3">
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">First Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="First Name"
                  value={order.firstname}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Last Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Last Name"
                  value={order.lastname}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Email</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Email"
                  value={order.email}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Address</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Address"
                  value={order.address1}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Address 2</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Address"
                  value={order.address2}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">City</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Email"
                  value={order.email}
                  onChange={(e) => handleChange(e)}
                  name="productName"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}}>Category</label>
                <select
                  className="form-control"
                  name="productCategoryName"
                  value={order.productCategoryName}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>
              <div className="form-group">
                <label style={{color:"white"}} >Brand</label>
                <select
                  className="form-control"
                  name="productBrand"
                  value={order.productBrand}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlTextarea1">
                  Product Description
                </label>
                <textarea
                  name="productDescription"
                  className="form-control"
                  id="exampleFormControlTextarea1"
                  rows={3}
                  value={order.productDescription}
                  onChange={(e) => handleChange(e)}
                  placeholder="Product Description"
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}}>Warranty</label>
                <select
                  className="form-control"
                  name="productWarranty"
                  value={order.productWarranty}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>




              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">RAM</label>
                <input
                  name="productRam"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="RAM"
                  min={1}
                  value={order.productRam}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Storage</label>
                <input
                  name="productStorage"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Storage"
                  min={1}
                  value={order.productStorage}
                  onChange={(e) => handleChange(e)}
                />
              </div>

              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Battery</label>
                <input
                  name="productBattery"
                  type="text"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Battery"
                  min={1}
                  value={order.productBattery}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Display</label>
                <input
                  name="productDisplay"
                  type="text"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Display"
                  min={1}
                  value={order.productDisplay}
                  onChange={(e) => handleChange(e)}
                />
              </div>

              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Cost Price</label>
                <input
                  name="productCostPrice"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Cost Price"
                  min={1}
                  value={order.productCostPrice}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Retail Price</label>
                <input
                  type="number"
                  name="productRetailPrice"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Retail Price"
                  min={1}
                  value={order.productRetailPrice}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label style={{color:"white"}} htmlFor="exampleFormControlInput1">Quantity</label>
                <input
                  name="productQuantity"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Quantity"
                  min={1}
                  value={order.productQuantity}
                  onChange={(e) => handleChange(e)}
                />
              </div>



              <div className="form-group">
                <button
                  name="save"
                  className="btn btn-warning btn-block"
                  onClick={saveOrder}
                >
                  <i class="fa fa-paper-plane" aria-hidden="true"></i> Save
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
    </div>

  );
};

export default Checkout;
