import { combineReducers, createStore } from "redux";
import rootReducers from "./reducers";
import addItems from "./reducers/addItem";



const reducers = combineReducers({

    cart:addItems
})




let initialState = {

    cart:{
        addItem:localStorage.getItem("addItems") 

    }
}

const store = createStore(rootReducers,reducers);




export default store;
