const addItem = [];

const addItems = (state = addItem, action) => {
    switch (action.type) {
        case "ADDITEM" : 
        const item = action.payload
        return{
            ...state,
            addItem:[...state.addItem,item]
        }
        break;

        case "DELITEM" :
            return state = state.filter((x)=>{
                return x.id !== action.payload.id
            })
        break;

        default: return state;
        break;


    }
}

export default addItems;
