import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import ProductService from "../service/ProductService";
import OrderService from "../service/OrderService";
import { toast } from "react-toastify";
//import { Link, useNavigate } from "react-router-dom";

const Checkout = () => {
  const [products, setProducts] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);

      try {
        const response = await OrderService.getAllOrders();
        setProducts(response.data);
        console.log(response.data);
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  const notify = () => {
    toast.success(`Category Added Successfully`, {
      position: toast.POSITION.TOP_RIGHT,
      autoClose: 1500,
    });
  };

  const [order, setOrder] = useState({
    firstname: "",
    lastname: "",
    email: "",
    address1: "",
    address2: "",
    paymentType: "",
    cardName: "",
    cardNumber: "",
    expiration: "",
    cvv: "",
    totalPrice: "",
  });

  // const [selects, setSelects] = useState()
  //   const navigate = useNavigate();

  const handleChange = (e) => {
    const value = e.target.value;
    setOrder({ ...order, [e.target.name]: value });
  };

  const saveOrder = (e) => {
    OrderService.saveOrder(order)
      .then((result) => {
        notify();
     <link rel="stylesheet" href="/checkout" />
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const state = useSelector((state) => state.addItem);

  var total = 0;
  const itemList = (item) => {
    total = total + item.productRetailPrice;
    return (
      <li className="list-group-item d-flex justify-content-between lh-sm">
        <div>
          <h6 className="my-0">{item.productName}</h6>
        </div>
        <span className="text-muted">Rs.{item.productRetailPrice}</span>
      </li>
    );
  };

  return (
    <div>

     <div className="content-wrapper" style={{background:"#181a1b"}}>
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-6 offset-md-3">
              <h1 className="m-0 text-center text-white">Add Products</h1>
            </div>
            {/* <div className="col-sm-6">
              <ol className="breadcrumb float-sm-right">
                <li className="breadcrumb-item">
                  <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item active">Dashboard</li>
              </ol>
            </div> */}
          </div>
        </div>
      </div>
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-6 offset-md-3">
              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Product Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Product Name"
                  value={order.orderName}
                  onChange={(e) => handleChange(e)}
                  name="orderName"
                />
              </div>
              <div className="form-group">
                <label>Category</label>
                <select
                  className="form-control"
                  name="orderCategoryName"
                  value={order.orderCategoryName}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>
              <div className="form-group">
                <label>Brand</label>
                <select
                  className="form-control"
                  name="orderBrand"
                  value={order.orderBrand}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlTextarea1">
                  order Description
                </label>
                <textarea
                  name="orderDescription"
                  className="form-control"
                  id="exampleFormControlTextarea1"
                  rows={3}
                  value={order.orderDescription}
                  onChange={(e) => handleChange(e)}
                  placeholder="order Description"
                />
              </div>
              <div className="form-group">
                <label>Warranty</label>
                <select
                  className="form-control"
                  name="orderWarranty"
                  value={order.orderWarranty}
                  onChange={(e) => handleChange(e)}
                >
                  <option value="Mobile Phone">Mobile Phone</option>
                  <option value="Phone Accessories">Phone Accessories</option>
                  <option value="Smart Devices">Smart Devices</option>
                </select>
              </div>




              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">RAM</label>
                <input
                  name="orderRam"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="RAM"
                  min={1}
                  value={order.orderRam}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Storage</label>
                <input
                  name="orderStorage"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Storage"
                  min={1}
                  value={order.orderStorage}
                  onChange={(e) => handleChange(e)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Battery</label>
                <input
                  name="orderBattery"
                  type="text"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Battery"
                  min={1}
                  value={order.orderBattery}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Display</label>
                <input
                  name="orderDisplay"
                  type="text"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Display"
                  min={1}
                  value={order.orderDisplay}
                  onChange={(e) => handleChange(e)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Cost Price</label>
                <input
                  name="orderCostPrice"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Cost Price"
                  min={1}
                  value={order.orderCostPrice}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Retail Price</label>
                <input
                  type="number"
                  name="orderRetailPrice"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Retail Price"
                  min={1}
                  value={order.orderRetailPrice}
                  onChange={(e) => handleChange(e)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="exampleFormControlInput1">Quantity</label>
                <input
                  name="orderQuantity"
                  type="number"
                  className="form-control"
                  id="exampleFormControlInput1"
                  placeholder="Quantity"
                  min={1}
                  value={order.orderQuantity}
                  onChange={(e) => handleChange(e)}
                />
              </div>



              <div className="form-group">
                <button
                  name="save"
                  className="btn btn-warning btn-block"
                  onClick={saveOrder}
                >
                  <i class="fa fa-paper-plane" aria-hidden="true"></i> Save
                </button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
    </div>
  );
};

