import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import ProductService from "../service/ProductService";
import OrderService from "../service/OrderService";
import { toast } from "react-toastify";
//import { Link, useNavigate } from "react-router-dom";

const Checkout = () => {
  const [products, setProducts] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);

      try {
        const response = await OrderService.getAllOrders();
        setProducts(response.data);
        console.log(response.data);
      } catch (error) {
        console.log(error);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  const notify = () => {
    toast.success(`Category Added Successfully`, {
      position: toast.POSITION.TOP_RIGHT,
      autoClose: 1500,
    });
  };

  const [order, setOrder] = useState({
    firstname: "",
    lastname: "",
    email: "",
    address1: "",
    address2: "",
    paymentType: "",
    cardName: "",
    cardNumber: "",
    expiration: "",
    cvv: "",
    totalPrice: "",
  });

  // const [selects, setSelects] = useState()
  //   const navigate = useNavigate();

  const handleChange = (e) => {
    const value = e.target.value;
    setOrder({ ...order, [e.target.name]: value });
  };

  const saveOrder = (e) => {
    OrderService.saveOrder(order)
      .then((result) => {
        notify();
     <link rel="stylesheet" href="/checkout" />
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const state = useSelector((state) => state.addItem);

  var total = 0;
  const itemList = (item) => {
    total = total + item.productRetailPrice;
    return (
      <li className="list-group-item d-flex justify-content-between lh-sm">
        <div>
          <h6 className="my-0">{item.productName}</h6>
        </div>
        <span className="text-muted">Rs.{item.productRetailPrice}</span>
      </li>
    );
  };

  return (

      <div className="container my-5">
        <div className="row g-5">
          <div className="col-md-5 col-lg-4 order-md-last">
            <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text" style={{ color: "white" }}>
                Your cart
              </span>
              <span className="badge bg-warning rounded-pill">
                {state.length}
              </span>
            </h4>
            <ul className="list-group mb-3">
              {state.map(itemList)}

              <li className="list-group-item d-flex justify-content-between">
                <span>Total</span>
                <strong>Rs.{total}</strong>
              </li>
            </ul>

            {/* <form className="card p-2">
                            <div className="input-group">
                                <input type="text" className="form-control" placeholder="Promo code" />
                                <button type="submit" className="btn btn-secondary">Redeem</button>
                            </div>
                        </form> */}
          </div>
          <div className="content-wrapper" style={{background:"#181a1b"}}>
      <div className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-6 offset-md-3">
              <h1 className="m-0 text-center text-white">Add Products</h1>
            </div>
            {/* <div className="col-sm-6">
              <ol className="breadcrumb float-sm-right">
                <li className="breadcrumb-item">
                  <Link to="/">Home</Link>
                </li>
                <li className="breadcrumb-item active">Dashboard</li>
              </ol>
            </div> */}
          </div>
        </div>
      </div>
      <section className="content">
        <div className="container-fluid">
